import React from "react";
import Web3 from "web3";
import Tether from "./../truffle_abis/Tether.json";
const App = () => {
  const [account, setAccount] = React.useState("0x0");
  const [objectInformation, setObjectInformation] = React.useState({
    tether: {},
    rwd: {},
    decentralBank: {},
    tetherBalance: "0",
    rwdBalance: "0",
    stakingBalance: "0",
    loading: true,
  });
  const handleCheckWeb3 = async () => {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum);
      await window.ethereum.enable();
    } else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider);
    } else {
      window.alert("No ethereum browser detected! You can check out MetaMask!");
    }
  };
  const getListAccountsMetaMask = async () => {
    const web3 = window.web3;
    // get list accounts metamask
    const accountList = await web3.eth.getAccounts();
    account && account.length > 0 && setAccount(accountList[0]);
  };
  const handleLoadTetherContract = async () => {
    const web3 = window.web3;
    // get network id in file Tether.json from key networks
    const networkID = await web3.eth.net.getId();
    const tetherData = Tether.networks[networkID];
    if (tetherData) {
      const tether = web3.eth.Contract(Tether.abi, tetherData.address);
      console.log(tether);
    }
  };
  const loadBlockchainData = async () => {
    await getListAccountsMetaMask();
    await handleLoadTetherContract();
  };
  const handleLoad = async () => {
    await handleCheckWeb3();
    await loadBlockchainData();
  };
  React.useEffect(() => {
    handleLoad();
  }, []);
  return (
    <>
      <p>
        Address <strong>{account}</strong>
      </p>
    </>
  );
};
export default App;
