import React from "react";
import Web3 from "web3";
const App = () => {
  const [account, setAccount] = React.useState("0x0");
  const handleCheckWeb3 = async () => {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum);
      await window.ethereum.enable();
    } else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider);
    } else {
      window.alert("No ethereum browser detected! You can check out MetaMask!");
    }
  };
  const loadBlockchainData = async () => {
    const web3 = window.web3;
    const accountList = await web3.eth.getAccounts();
    account && account.length > 0 && setAccount(accountList[0]);
  };
  const handleLoad = async () => {
    await handleCheckWeb3();
    await loadBlockchainData();
  };
  React.useEffect(() => {
    handleLoad();
  }, []);
  return (
    <>
      <p>
        Address <strong>{account}</strong>
      </p>
    </>
  );
};
export default App;
